select bu.id, bu.username, bu.mobile, bu.work_number workNumber, bu.in_service_status inServiceStatus, bu.department_name department, bu.time_of_entry timeOfEntry , bu.form_of_employment formOfEmployment , sauss.current_basic_salary wageBase, sauss.current_basic_salary currentBasicSalary, sauss.current_post_wage currentPostWage, case when sauss.current_basic_salary=0 or sauss.current_basic_salary is null then 0 when sauss.current_basic_salary>0 then 1 end isFixed from bs_user bu LEFT JOIN sa_user_salary sauss ON bu.id=sauss.user_id WHERE bu.company_id = #{companyId}
and bu.department_id in
#{id}
and bu.in_service_status in
#{id}
and bu.username like #{keyword}
limit #{page},#{pagesize}

